.burger {
  display: none;
  cursor: pointer;
  position: relative;
  cursor: pointer;
  width: 30px;
  height: 30px;
}

.burger div {
  width: 100%;
  height: 3px;
  background-color: white;
  margin: 5px;
  position: absolute;
  transition: all 0.3s;
  left: 50%;
  margin-left: -15px;
  z-index: 30;
}

.line1 {
  top: 0%;
}

.line2 {
  top: 33%;
}

.line3 {
  top: 66%;
}

/* Link Details*/
.nav-link::before {
  content: "";
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: 0;
  left: 0;
  visibility: hidden;
  transform: scaleX(0);
  transition: all 0.3s ease-in-out 0s;
}

.nav-link:focus::before,
.nav-link:hover::before {
  background-color: var(--highlightcolor01);
  visibility: visible;
  -webkit-transform: scaleX(1);
  transform: scaleX(1);
}

/* Mobile */
