    position: absolute;
    right: 0px;
    height: 260px;
    top: 8vh;
    background-color: black;
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    padding-top: 50px;
    transform: scale(1, 0);
    transform-origin: top;
    transition: transform 0.3s ease-in;
    z-index: 2;
    justify-content: flex-start;
  }

  .burger {
    display: block;
  }

  .nav-active {
    transform: scale(1, 1);
  }

  .burger-active .line1 {
    transform: rotate(45deg);
    top: 33%;
  }

  .burger-active .line2,
